mapper.select:
select
emp_no, birth_date, first_name, last_name, gender, hire_date
from employees


        and emp_no = #{empNo}


        and birth_date = #{birthDate}


        and first_name = #{firstName}


        and last_name = #{lastName}


        and gender = #{gender}


        and hire_date = #{hireDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from employees


        and emp_no = #{empNo}


        and birth_date = #{birthDate}


        and first_name = #{firstName}


        and last_name = #{lastName}


        and gender = #{gender}


        and hire_date = #{hireDate}
